export const getSEOConfigForCategory = (params: DifficultyParams): SEOConfig => {
    const { category, difficulty } = params;
    
    // Priority 1: Check for difficulty-based SEO
    if (difficulty && difficultyBasedSEOConfig[difficulty]) {
        return difficultyBasedSEOConfig[difficulty];
    }
    
    // Priority 2: Check for category-based SEO
    if (category && mockExamsSEOConfig[category]) {
        return mockExamsSEOConfig[category];
    }
    
    // Priority 3: Return default SEO
    return mockExamsSEOConfig.default;
};

