export const getSEOForExamPath = (pathId: string | null): SEOConfig | null => {
    if (!pathId) return null;
    const variants = [pathId, `exams/${pathId}`].map(v => v.toLowerCase());
    for (const [key, value] of Object.entries(examSEOConfig)) {
        const k = key.toLowerCase();
        if (variants.some(v => v.startsWith(k))) {
            return value;
        }
    }
    return null;
};
